mapper.update:
UPDATE REPLY SET REPLY_STATUS_CD = #{status}
WHERE
REPLY_NO = #{replyNo}

mapper.select:
SELECT REPLY_STATUS_CD
FROM REPLY
WHERE REPLY_NO = #{replyNo}

SELECT COUNT(*) FROM REPLY
WHERE REPLY_NO = #{inputResult}
WHERE POST_NO = #{inputResult}
JOIN MEMBER USING(MEMBER_NO) WHERE MEMBER_NM LIKE #{inputResult}||'%' OR MEMBER_NM LIKE '%'||#{inputResult}||'%'
WHERE MEMBER_NO = #{inputResult}
JOIN REPLY_STATUS USING(REPLY_STATUS_CD) WHERE REPLY_STATUS_NM LIKE '%'|| #{inputResult}||'%' OR REPLY_STATUS_NM #{inputResult}||'%'

SELECT REPLY_NO , REPLY_CONTENT , REPLY_CREATE_DT , LIKE_COUNT , POST_NO , MEMBER_NO , REPLY_STATUS_CD , REPLY_STATUS_NM , MEMBER_NM , MEMBER_NICKNM FROM REPLY JOIN MEMBER USING(MEMBER_NO) JOIN REPLY_STATUS USING(REPLY_STATUS_CD)
WHERE REPLY_NO = #{inputResult}
WHERE POST_NO = #{inputResult}
WHERE MEMBER_NM LIKE #{inputResult}||'%' OR MEMBER_NM LIKE '%'||#{inputResult}||'%'
WHERE MEMBER_NO = #{inputResult}
WHERE REPLY_STATUS_NM LIKE '%'|| #{inputResult}||'%'
ORDER BY REPLY_NO DESC

SELECT COUNT(*) FROM POST
WHERE MEMBER_NO = #{inputResult}
JOIN MEMBER USING(MEMBER_NO) WHERE MEMBER_NM LIKE #{inputResult}||'%' OR MEMBER_NM LIKE '%'||#{inputResult}||'%'
JOIN MEMBER USING(MEMBER_NO) WHERE MEMBER_NICKNM LIKE #{inputResult}||'%' OR MEMBER_NICKNM LIKE '%'||#{inputResult}||'%'
WHERE POST_NO = #{inputResult}
JOIN POST_STATUS USING(POST_STATUS_CD) WHERE POST_STATUS_NM LIKE '%'||#{inputResult}||'%' OR POST_STATUS_NM LIKE #{inputResult}||'%'

mapper.update:
UPDATE POST SET POST_STATUS_CD = #{status}
WHERE
POST_NO = #{postNo}

mapper.select:
SELECT COUNT(*) FROM REPORT
WHERE MEMBER_NO = #{inputResult}
JOIN MEMBER USING(MEMBER_NO) WHERE MEMBER_NM LIKE #{inputResult}||'%' OR MEMBER_NM LIKE '%'||#{inputResult}||'%'
JOIN REPORT_TYPE USING(REPORT_TYPE_NO) WHERE REPORT_TYPE_NM LIKE #{inputResult}||'%'
WHERE REPORT_NO = #{inputResult}

SELECT REPORT_NO , REPORT.MEMBER_NO , REPORT_CONTENT , REPORT.CREATE_DT , REPORT_TYPE_NO , MEMBER_NICKNM ,TARGET_PK,MEMBER_NM, REPORT_TYPE_NM FROM REPORT JOIN REPORT_TYPE USING(REPORT_TYPE_NO) LEFT JOIN POST ON(REPORT.TARGET_PK = POST.POST_NO) JOIN MEMBER ON(REPORT.MEMBER_NO = MEMBER.MEMBER_NO)
WHERE REPORT.MEMBER_NO = #{inputResult}
WHERE MEMBER_NM LIKE #{inputResult}||'%' OR MEMBER_NM LIKE '%'||#{inputResult}||'%'
WHERE REPORT_TYPE_NM LIKE #{inputResult}||'%'
WHERE REPORT_NO = #{inputResult}
ORDER BY REPORT_NO DESC
