mapper.select:
SELECT COUNT(*) FROM POST




			WHERE MEMBER_NO = #{inputResult}



			WHERE MEMBER_NM LIKE #{inputResult}||'%'
			OR MEMBER_NM
			LIKE '%'||#{inputResult}||'%'




			WHERE POST_NO = #{inputResult}


			WHERE POST_STATUS_CD = #{inputResult}

SELECT POST_NO , MEMBER_NM, POST_CONTENT , READ_COUNT , LIKE_COUNT, TO_CHAR(POST.CREATE_DT , 'YYYY-MM-DD') CREATE_DT, TO_CHAR(POST.MODIFY_DT , 'YYYY-MM-DD') MODIFY_DT, MEMBER_NO , POST_STATUS_NM, POST_STATUS_CD FROM POST JOIN POST_STATUS USING(POST_STATUS_CD) JOIN MEMBER USING (MEMBER_NO)
WHERE MEMBER_NO = #{inputResult}
WHERE MEMBER_NM LIKE #{inputResult}||'%' OR MEMBER_NM LIKE '%'||#{inputResult}||'%'
WHERE POST_NO = #{inputResult}
WHERE POST_STATUS_CD = #{inputResult}
ORDER BY POST_NO DESC

mapper.update:
UPDATE POST SET POST_STATUS_CD = #{status}
WHERE
POST_NO = #{postNo}
