mapper.update:
UPDATE MEMBER SET
MEMBER_NICKNM = #{memberNickName},
MEMBER_BIRTH = #{memberBirth}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_PW = #{newPw}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER_IMG SET
IMG_PATH = #{imgPath},
IMG_NM = #{imgName},
IMG_ORIGINAL = #{imgOriginal},
IMG_LEVEL=#{imgLevel}
WHERE MEMBER_NO = #{memberNo}

mapper.delete:
DELETE FROM MEMBER_IMG
WHERE MEMBER_NO = #{MemberNo}
AND IMG_LEVEL IN ( ${deleteImages} )
