mapper.select:
 	 	SELECT COUNT(TAG_NM)
		FROM TAG
		WHERE TAG_NM = #{tagName}

 	SELECT COUNT(MOVIE_NO)
	FROM MOVIE
	WHERE MOVIE_NO = ${movieNo}

SELECT COUNT(*)
FROM "LIKE"
WHERE POST_NO = #{postNo}
AND MEMBER_NO = #{memberNo}

SELECT COUNT(MEMBER_NICKNM)
FROM MEMBER
WHERE MEMBER_NICKNM = #{memberName}

SELECT MEMBER_NO
FROM MEMBER
WHERE MEMBER_NICKNM = #{memberName}
